import React, { Component, useState } from 'react';
import {

    Card,
    CardImg,
    CardText,
    CardBody,
    CardTitle,
    CardSubtitle,
    Button
} from 'reactstrap';

import {

    Modal,
    ModalHeader,
    ModalBody,
    ModalFooter
} from 'reactstrap';

export class GameCard extends Component {
    constructor(props) {
        super(props);
    }

    render() {

        const [modal, setModal] = useState(false);
        const toggle = () => setModal(!modal);

        return (

            <div>
                <Card>
                    <CardImg top width="20%" src={this.props.games.screenshot} alt="Card image cap" />
                    <CardBody>

                        <CardTitle>{this.props.games.name}</CardTitle>
                        <CardText>{this.props.games.id}</CardText>

                        <Button
                            color="primary"
                            onClick={() => this.props.removeGame(this.props.games.id)}
                        >
                            Delete
                        </Button>

                        <div>
                            <Button color="danger" onClick={toggle}>Modal Test</Button>

                            <Modal isOpen={modal} toggle={toggle} className="modaltest">

                                <ModalHeader toggle={toggle}>Modal title</ModalHeader>

                                <ModalBody>
                                    Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                                </ModalBody>

                                <ModalFooter>
                                    <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
                                    <Button color="secondary" onClick={toggle}>Cancel</Button>
                                </ModalFooter>

                            </Modal>
                        </div>

                    </CardBody>
                </Card>
            </div>

        );
    }
}

export default GameCard;
